import React, { useState } from "react";
import Entry from "./Entry";
import "./app.css";
import { resolvePath } from "react-router-dom";
const App = () => {

  let [code, setCode] = useState();

  //save the entries logged by the user
  const saveMoments = async () => {
    console.log("saveMoments called");
    let response = await fetch('http://127.0.0.1:8086/api/entries/saveEntries',{
      method: 'POST',
      body: JSON.stringify({
        "userId": "640b4c145cb0d00c04a7938b",
        "entry1" : "grateful for this beautiful day",
        "entry2" : "grateful to Lord for protecting us!!",
        "entry3" : "grateful for this new day and opportunity!"
      }),
      headers:{
        'Content-type': 'application/json;charset=UTF-8',
      },
    });
    let data = await response.json();
    console.log(data+" "+response.status);
    setCode(response.sta)
  }

  return (
  <div className=" w-3/5 rounded-xl bg-orange-100 p-6 shadow-lg">
    <div>
      <div className="pb-5 text-center text-sm font-medium tracking-wider text-sky-900">I AM GRATEFUL FOR:</div>
      <Entry value="1."/>
      <Entry value="2."/>
      <Entry value="3." />
     
    </div>
    <div>
     <button className="text-center mx-auto px-4 py-1 text-sm text-rose-600 font-semibold rounded-full border border-rose-200 hover:text-white hover:bg-rose-600 hover:border-transparent focus:outline-none focus:ring-2 focus:ring-rose-600 focus:ring-offset-2"
     onClick={saveMoments}>record</button>
     </div>
  </div>
  );
}

export default App;
